mapper.select:
select  from zd_respect


    and id_card like '%${idCard}%'


    and change_state = #{changeState}


    and name like '%${name}%'


    and community_id = #{communityId}


    and phone = #{phone}


order by create_time desc

  limit #{start},#{end}

select count(*) from zd_roster


    and id_card like '%${idCard}%'


    and name like '%${name}%'


    and community_id = #{communityId}


    and phone = #{phone}
